//import React from "react";// utilise component class
import { useState } from "react";
import Header from "./component/Header";
import Tasks from "./component/Tasks";
import AddTask from "./component/AddTask";

//COMPONENT CLASS
/*
class App extends React.Component{
  render(){
   return <h1>hello tous le monde</h1>
  }
}
*/
function App(onAdd) {
  const [showAddTask, setShowAddTask] = useState(false);
  const [tasks, setTasks] = useState([
    {
      id: 1,
      text: "l'orange",
      date: "3/05/2022",
      reminder: true,
    },
    {
      id: 2,
      text: "la mangue",
      date: "3/05/2022",
      reminder: true,
    },
    {
      id: 3,
      text: "le banane",
      date: "12/01/2022",
      reminder: true,
    },
  ]);

  // ajout tache
  const addTask = (task) => {
    const id = Math.floor(Math.random() * 1000 + 1);
    const newTask = { id, ...task };
    console.log(newTask);
    setTasks([...tasks, newTask]);
  };

  //supprimer tache
  const deleteTask = (id) => {
    console.log("delete", id);

    setTasks(tasks.filter((task) => task.id !== id));
  };
  //toggle tache
  const toggleReminder = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, reminder: !task.reminder } : task
      )
    );
  };
  return (
    <div className="container">
      <Header onAdd={()=>setShowAddTask(!showAddTask)} showAdd={showAddTask} />
      {/* qd afficher la tache est vrai showAddTask et puis montrer ce composant ici ce faux donc il va pas afficher le formulaire */}
      {showAddTask && <AddTask onAdd={addTask} />}
      {tasks.length > 0 ? (
        <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder} />
      ) : (
        "pas des taches enregistrer"
      )}
    </div>
  );
}
export default App;
