//import React from "react";// utilise component class
import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./component/Header";

import Tasks from "./component/Tasks";
import AddTask from "./component/AddTask";
import About from "./component/About";
import Footer from "./component/Footer";
//COMPONENT CLASS
/*
class App extends React.Component{
  render(){
   return <h1>hello tous le monde</h1>
  }
}
*/
function App(onAdd) {
  const [showAddTask, setShowAddTask] = useState(false);
  const [tasks, setTasks] = useState([]);

  // useEffect(()=>{
  //   const fetchTasks= async ()=>{
  //const res= await fetch('http://localhost:5000/tasks')
  //const data= await res.json()
  //console.log(data)
  //console.log(res)
  // }
  //fetchTasks()
  // },[])
  useEffect(() => {
    const getTasks = async () => {
      const tasksFromServer = await fetchTasks();
      setTasks(tasksFromServer);
    };
    getTasks();
  }, []);
  // fetch Tasks

  const fetchTasks = async () => {
    const res = await fetch("http://localhost:5000/tasks");
    const data = await res.json();
    console.log(data);
    return data;
  };

  // fetch task obtenir qu'une seul tache lorqu'on double clique il y'a une  bordure verte là ce ui design ici on le code coté serveur

  const fetchTask = async (id) => {
    const res = await fetch(`http://localhost:5000/tasks/${id}`);
    const data = await res.json();
    console.log(data);
    return data;
  };

  /* avant la creation de mon backend 
  const [tasks, setTasks] = useState([
    {
      id: 1,
      text: "l'orange",
      date: "3/05/2022",
      reminder: true,
    },
    {
      id: 2,
      text: "la mangue",
      date: "3/05/2022",
      reminder: true,
    },
    {
      id: 3,
      text: "le banane",
      date: "12/01/2022",
      reminder: true,
    },
  ]);
  */

  // ajout tache
  const addTask = async (task) => {
    const res = await fetch("http://localhost:5000/tasks", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(task),
    });
    const data = await res.json();
    setTasks([...tasks, data]);
    console.log(data);
    // const id = Math.floor(Math.random() * 1000 + 1);
    // const newTask = { id, ...task };
    // console.log(newTask);
    // setTasks([...tasks, newTask]);
  };
  //supprime simplement FRONTEND
  /*
  const deleteTask = (id) => {
    console.log("delete", id);

    setTasks(tasks.filter((task) => task.id !== id));
  };
  */

  //METHODE DELETE lES tache sur le serveur mm si on actualise la page il garde que les taches qui été au paravant EX: ON 3 Taches on effacer 1 il reste deux en actulisant on resteras tjr qu'avec 2 taches

  const deleteTask = async (id) => {
    console.log("delete", id);
    await fetch(`http://localhost:5000/tasks/${id}`, {
      method: "DELETE",
    });
    setTasks(tasks.filter((task) => task.id !== id));
  };
  //toggle tache
  const toggleReminder = async (id) => {
    const taskToToggle = await fetchTask(id);
    const updateTask = { ...taskToToggle, reminder: !taskToToggle.reminder };
    const res = await fetch(`http://localhost:5000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(updateTask),
    });
    const data = await res.json();
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, reminder: data.reminder } : task
      )
    );
  };
  return (
    <Router>
      <div className="container">
        <Header
          onAdd={() => setShowAddTask(!showAddTask)}
          showAdd={showAddTask}
        />
        {/* qd afficher la tache est vrai showAddTask et puis montrer ce composant ici ce faux donc il va pas afficher le formulaire */}
        {showAddTask && <AddTask onAdd={addTask} />}
        {tasks.length > 0 ? (
          <Tasks
            tasks={tasks}
            onDelete={deleteTask}
            onToggle={toggleReminder}
          />
        ) : (
          "pas des taches enregistrer"
        )}
        <Routes>
       < Route path="/" exact render={(props)}/>

          <Route path="/about" element={<About />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}
export default App;
