const Header = ({title,onAdd,showAdd}) => {

  return (
    <header className="header">
        <h1 >{title} </h1>
        <Buttons onClick={onAdd}  color={showAdd ? "red" : "green"} text={showAdd ? "fermer" : "ajouter"}/>
    </header>
  )
}
