import { useState } from 'react'

const Task = () => {
    const [tasks,setTasks]= useState([
        {
            id:1,
            text:"l'orange",
            date: "3/05/2022",
            reminder:true
        },{
            id:2,
            text:"la mangue",
            date: "3/05/2022",
            reminder:true
        },{
            id:3,
            text:"le banane",
            date: "12/01/2022",
            reminder:true
        }
    ])
  return ( <>
    {tasks.map((task)=>(<h3 key={task.id}>{task.text}</h3>))
    }

    </>
  )
}

export default Task
