class ClassState extends Component{

    constructor(props){
        super(props)

        this.state={
            counter:0
        }
    }

    addOne=()=>{

       this.setState((prevState) =>{

        return{
            counter: prevState.counter+1
        }



       })
    }

    render(){
        return(
            <div>
                <h1> hello : {this.state.counter}</h1> 
                <button onClick={this.addOne}>click: me</button>
                </div>
        )
    }
}

export default ClassState
