import {useState} from 'react'

const AddTask = ({onAdd}) => {
    const [text,setText]= useState("")
    const [day,setDay]= useState("")
    const [reminder,setReminder]= useState(false)

    const onSubmit=(e)=>{
        e.preventDefault()

        if(!text){
            alert(" veuilez entrez une tache")
        }
        onAdd({text,day,reminder})
        setText("")
        setDay("")


    }
  return (
   <form className="add-form" onSubmit={onSubmit}>
       <div className="form-control">
           <label htmlFor="">Task</label>
           <input type="text"  placeholder="Add-Task" value={text} onChange={(e)=> setText(e.target.value)}/>

       </div>
       <div className="form-control">
           <label htmlFor="">Day and time</label>
           <input type="text"  placeholder="Day and time" value={day} onChange={(e)=> setDay(e.target.value)}/>

       </div>
       <div className="form-control form-control-check">
           <label htmlFor="">Set Reminder</label>
           <input type="checkbox" value={reminder} checked={reminder} onChange={(e)=> setReminder(e.currentTarget.checked)} />

       </div>
       <input type="submit" value='saveTask' className="btn btn-block"/>

   </form>
  )
}

export default AddTask
